INSERT INTO `job` VALUES ('财务分析控制', '全职', '人事/行政/财务/法务', '北京海淀区', '北京中盛旭鑫环境能源科技有限公司', '面议', '本科', '不限', 'chenwenya7@163.com', '13262733597', '\r\n\r\n1、财务会计、金融、经济等相关专业全日制大学专科以上毕业；\r\n2、拥有会计资格证，能独立完成会计所有工作；\r\n个人形象良好，身体健康，五官端正，工作踏实，认真细心，积极主动；具有良好的职业操守及团队合作精神，具有较强的沟通、理解、分析和工作能力。', '\r\n1、编制公司会计凭证，填制会计账册，编制会计报表；\r\n2、进行公司财务分析及会计核算；\r\n3、财会文件的准备、归档和保管；\r\n4、实施日常费用报销程序；\r\n5、核实银行存、货款入账；\r\n6、盘点库存现金核对账本；\r\n7、按期缴纳公司各项费用；\r\n8、协助经理完成公司其他日常事务性工作及公司要求的工作', '无', '陈文雅');
