INSERT INTO `job` VALUES ('集成测试/交付工程师', '全职', '互联网/通讯及硬件', '苏州', '华砺智行（武汉）科技有限公司', '10000-15000元', '本科', '5年以上', 'hr@huali-tec.com', '0512-87670796', '无', '1、参与车联网（车路协同）端到端产品的测试，产品包括终端，边缘计算服务，平台等；\r\n\r\n2、测试任务包含测试用例设计和执行，以及问题上报和跟踪；\r\n\r\n3、负责软件类产品安装、操作、QA等文档类说明书编写；\r\n\r\n4、负责软件类产品测试、交付，提供包括但不限于线上、电话、本地等技术支持；\r\n\r\n5、完成现场技术支持方案编写与设计；\r\n\r\n6、在现场按计划和进度完成系统部署；\r\n\r\n7、在现场执行软件产品系统测试及巡检，及时发现缺陷，并使用缺陷管理工具登记缺陷；\r\n\r\n8、编写现场部署总结报告；\r\n\r\n9、向现场运维人员和客户执行系统的培训。\r\n\r\n10、能够在项目现场进行软件部署和调试，并对现场问题进行初步定位\r\n\r\n11、及时反馈现场问题，并配合开发人员进行问题分析 \r\n', '五险一金、带薪休假（满一年后可增加一天）、节日礼品、生日礼物、年度体检等', '李女士');
INSERT INTO `job` VALUES ('项目技术员', '全职', '客服/技术支持', '北京', '北京国环莱茵环保科技股份有限公司', '10000-15000元', '本科', '不限', 'z707577587@163.com', '13466762108', '要求：环境工程、给排水相关专业大专以上学历，吃苦耐劳，有良好的沟通能力和表达能力，服从公司安排，能适应长期出差。', '职责：\r\n1、从事垃圾渗滤液、生活污水、工业废水等的工程项目施工管控工作；\r\n2、协助项目经理准备各种项目相关的手续、资料；\r\n3、负责现场收货及库管工作；\r\n4、协助进行设备安装调试；\r\n5、交工验收前的设备调试、业务培训、试运行。', '无', '张小帆');
